 183 | function getCardType(cards) {
 184 |     const n = cards.length;
 185 |     if (n === 0) return null;
 186 |     
 187 |     const ranks = cards.map(c => c.rank).sort((a, b) => a - b);
 188 |     const countMap = {};
 189 |     ranks.forEach(r => countMap[r] = (countMap[r] || 0) + 1);
 190 |     const counts = Object.values(countMap).sort((a, b) => b - a);
 191 |     const uniqueRanks = Object.keys(countMap).map(Number).sort((a, b) => a - b);
 192 |     
 193 |     // 王炸
 194 |     if (n === 2 && ranks.includes(13) && ranks.includes(14)) {
 195 |         return { type: 'rocket', rank: 100 };
 196 |     }
 197 |     
 198 |     // 炸弹
 199 |     if (n === 4 && counts[0] === 4) {
 200 |         return { type: 'bomb', rank: ranks[0] };
 201 |     }
 202 |     
 203 |     // 单张
 204 |     if (n === 1) {
 205 |         return { type: 'single', rank: ranks[0] };
 206 |     }
 207 |     
 208 |     // 对子
 209 |     if (n === 2 && counts[0] === 2) {
 210 |         return { type: 'pair', rank: ranks[0] };
 211 |     }
 212 |     
 213 |     // 三张
 214 |     if (n === 3 && counts[0] === 3) {
 215 |         return { type: 'triple', rank: ranks[0] };
 216 |     }
 217 |     
 218 |     // 三带一
 219 |     if (n === 4 && counts[0] === 3) {
