 221 |         return { type: 'triple_one', rank: Number(tripleRank) };
 222 |     }
 223 |     
 224 |     // 三带二
 225 |     if (n === 5 && counts[0] === 3 && counts[1] === 2) {
 226 |         const tripleRank = Object.keys(countMap).find(k => countMap[k] === 3);
 227 |         return { type: 'triple_two', rank: Number(tripleRank) };
 228 |     }
 229 |     
 230 |     // 顺子（5张以上连续，不能有2和王）
 231 |     if (n >= 5 && counts.every(c => c === 1)) {
 232 |         if (ranks[n-1] <= 11 && isConsecutive(ranks)) {
 233 |             return { type: 'straight', rank: ranks[n-1], length: n };
 234 |         }
 235 |     }
 236 |     
 237 |     // 连对（3对以上连续）
 238 |     if (n >= 6 && n % 2 === 0 && counts.every(c => c === 2)) {
 239 |         if (uniqueRanks[uniqueRanks.length-1] <= 11 && isConsecutive(uniqueRanks)) {
 240 |             return { type: 'straight_pair', rank: uniqueRanks[uniqueRanks.length-1], length: n };
 241 |         }
 242 |     }
 243 |     
 244 |     // 飞机不带
 245 |     if (n >= 6 && n % 3 === 0 && counts.every(c => c === 3)) {
 246 |         if (uniqueRanks[uniqueRanks.length-1] <= 11 && isConsecutive(uniqueRanks)) {
 247 |             return { type: 'plane', rank: uniqueRanks[uniqueRanks.length-1], length: n };
 248 |         }
 249 |     }
 250 |     
 251 |     // 飞机带单
 252 |     if (n >= 8 && n % 4 === 0 && counts[0] === 3) {
 253 |         const triples = Object.keys(countMap).filter(k => countMap[k] === 3).map(Number).sort((a, b) => a - b);
 254 |         if (triples.length >= 2 && triples[triples.length-1] <= 11 && isConsecutive(triples)) {
 255 |             return { type: 'plane_single', rank: triples[triples.length-1], length: triples.length * 3 };
 256 |         }
 257 |     }
 258 |     
 259 |     // 飞机带对
 260 |     if (n >= 10 && n % 5 === 0 && counts[0] === 3) {
 261 |         const triples = Object.keys(countMap).filter(k => countMap[k] === 3).map(Number).sort((a, b) => a - b);
 262 |         const pairs = Object.keys(countMap).filter(k => countMap[k] === 2);
 263 |         if (triples.length >= 2 && pairs.length === triples.length && triples[triples.length-1] <= 11 && isConsecutive(triples)) {
 264 |             return { type: 'plane_pair', rank: triples[triples.length-1], length: triples.length * 3 };
 265 |         }
 266 |     }
 267 |     
 268 |     // 四带二单
 269 |     if (n === 6 && counts[0] === 4) {
 270 |         const fourRank = Object.keys(countMap).find(k => countMap[k] === 4);
 271 |         return { type: 'four_two_single', rank: Number(fourRank) };
 272 |     }
 273 |     
 274 |     // 四带二对
 275 |     if (n === 8 && counts[0] === 4 && counts.filter(c => c === 2).length === 2) {
 276 |         const fourRank = Object.keys(countMap).find(k => countMap[k] === 4);
 277 |         return { type: 'four_two_pair', rank: Number(fourRank) };
 278 |     }
