 291 | // 比较牌型
 292 | function canBeat(type1, type2) {
 293 |     // 王炸最大
 294 |     if (type1.type === 'rocket') return true;
 295 |     if (type2.type === 'rocket') return false;
 296 |     
 297 |     // 炸弹大于非炸弹
 298 |     if (type1.type === 'bomb' && type2.type !== 'bomb') return true;
 299 |     if (type2.type === 'bomb' && type1.type !== 'bomb') return false;
 300 |     
 301 |     // 同类型比较
 302 |     if (type1.type !== type2.type) return false;
 303 |     
 304 |     // 顺子、连对、飞机需要长度相同
 305 |     if (['straight', 'straight_pair', 'plane', 'plane_single', 'plane_pair'].includes(type1.type)) {
 306 |         if (type1.length !== type2.length) return false;
 307 |     }
 308 |     
 309 |     return type1.rank > type2.rank;
 310 | }
