 366 |     
 367 |     if (lastType.type === 'single') {
 368 |         for (let i = cards.length - 1; i >= 0; i--) {
 369 |             if (cards[i].rank > lastType.rank) return [cards[i]];
 370 |         }
 371 |     }
 372 |     
 373 |     if (lastType.type === 'pair') {
 374 |         for (let i = cards.length - 1; i >= 1; i--) {
 375 |             if (cards[i].rank === cards[i-1].rank && cards[i].rank > lastType.rank) return [cards[i], cards[i-1]];
 376 |         }
 377 |     }
