Thought: 
Action: Write[{"path": "tests/quick_test/doudizhu/style.css", "content": "* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Microsoft YaHei', 'Segoe UI', Arial, sans-serif;
    background: linear-gradient(135deg, #1e5631 0%, #0d3320 50%, #152a1c 100%);
    background-image: 
        radial-gradient(circle at 20% 80%, rgba(255,255,255,0.03) 0%, transparent 50%),
        radial-gradient(circle at 80% 20%, rgba(255,255,255,0.03) 0%, transparent 50%);
    min-height: 100vh;
    overflow: hidden;
}

.game-container {
    width: 100vw;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding: 20px;
    position: relative;
}

/* 玩家区域 */
.player-area {
    display: flex;
    flex-direction: column;
    align-items: center;
    position: relative;
}

.player-info {
    background: linear-gradient(135deg, rgba(30,30,30,0.9), rgba(50,50,50,0.85));
    backdrop-filter: blur(10px);
    color: white;
    padding: 10px 25px;
    border-radius: 30px;
    margin: 10px;
    display: flex;
    gap: 20px;
    align-items: center;
    font-size: 14px;
    border: 1px solid rgba(255,255,255,0.1);
    box-shadow: 0 8px 32px rgba(0,0,0,0.3);
}

.player-name {
    font-weight: 600;
    font-size: 15px;
}

.card-count {
    color: #ffd700;
    font-weight: 500;
}

.role {
    color: #ff6b6b;
    font-weight: bold;
    padding: 3px 12px;
    border-radius: 12px;
    background: rgba(255,107,107,0.2);
}

.role.landlord {
    color: #ffd700;
    background: rgba(255,215,0,0.2);
    text-shadow: 0 0 10px rgba(255,215,0,0.5);
}

.role.farmer {
    color: #4ecdc4;
    background: rgba(78,205,196,0.2);
}

/* 中间区域布局 */
.middle-area {
    flex: 1;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 80px;
}

.table-area {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    max-width: 600px;
}

.table-cards {
    background: linear-gradient(135deg, rgba(20,60,40,0.8), rgba(30,90,50,0.6));
    backdrop-filter: blur(15px);
    border-radius: 20px;
    padding: 30px;
    min-height: 140px;
    min-width: 350px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 25px;
    border: 2px solid rgba(255,255,255,0.1);
    box-shadow: 
        0 20px 60px rgba(0,0,0,0.4),
        inset 0 1px 0 rgba(255,255,255,0.1);
}

.table-tip {
    color: rgba(255,255,255,0.6);
    font-size: 18px;
    font-weight: 300;
    letter-spacing: 2px;
}

.game-info {
    color: white;
    font-size: 22px;
    text-align: center;
    font-weight: 500;
    text-shadow: 0 2px 10px rgba(0,0,0,0.3);
}

/* 扑克牌样式 */
.card {
    width: 70px;
    height: 98px;
    background: linear-gradient(145deg, #ffffff, #f8f8f8);
    border-radius: 10px;
    display: inline-flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: flex-start;
    padding: 6px 8px;
    margin: 0 -8px;
    box-shadow: 
        0 4px 15px rgba(0,0,0,0.25),
        inset 0 1px 0 rgba(255,255,255,1);
    cursor: pointer;
    transition: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    border: 1px solid rgba(0,0,0,0.1);
}

.card:hover {
    transform: translateY(-15px) scale(1.05);
    box-shadow: 
        0 15px 35px rgba(0,0,0,0.35),
        inset 0 1px 0 rgba(255,255,255,1);
    z-index: 10;
}

.card.selected {
    transform: translateY(-25px) scale(1.08);
    box-shadow: 
        0 20px 40px rgba(255,215,0,0.4),
        0 0 30px rgba(255,215,0,0.2),
        inset 0 1px 0 rgba(255,255,255,1);
    border: 3px solid #ffd700;
    z-index: 100;
}

.card.red {
    color: #e74c3c;
}

.card.black {
    color: #2c3e50;
}

.card-value {
    font-size: 20px;
    font-weight: bold;
    line-height: 1;
}

.card-suit {
    font-size: 18px;
    position: absolute;
    bottom: 8px;
    right: 8px;
}

.card.joker {
    font-size: 14px;
    font-weight: bold;
    writing-mode: vertical-rl;
}

/* 小王大王特殊样式 */
.card.joker-small {
    background: linear-gradient(145deg, #ffe4e1, #ffccd5);
    color: #2c3e50;
}

.card.joker-big {
    background: linear-gradient(145deg, #ffd700, #ffb347);
    color: #8b0000;
}

/* 玩家手牌区域 */
.bottom-player .player-cards {
    display: flex;
    justify-content: center;
    align-items: flex-end;
    min-height: 110px;
    margin-bottom: 15px;
    perspective: 1000px;
}

/* 电脑玩家手牌（背面） */
.computer-card {
    width: 45px;
    height: 63px;
    background: linear-gradient(145deg, #5dade2, #3498db);
    border-radius: 6px;
    margin: 0 -18px;
    border: 2px solid rgba(255,255,255,0.3);
    box-shadow: 0 3px 10px rgba(0,0,0,0.2);
}

/* 出过的牌 */
.played-cards {
    min-height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 15px 0;
}

.played-cards .card {
    margin: 0 -20px;
    cursor: default;
}

.played-cards .card:hover {
    transform: none;
}

.pass-text {
    color: rgba(255,255,255,0.7);
    font-size: 18px;
    font-weight: 600;
    letter-spacing: 3px;
    text-transform: uppercase;
}

/* 控制按钮区域 */
.control-area {
    display: flex;
    gap: 20px;
    margin-top: 20px;
}

.btn {
    padding: 14px 36px;
    font-size: 16px;
    font-weight: 600;
    border: none;
    border-radius: 30px;
    cursor: pointer;
