    top: 10px;
    left: 10px;
    right: 10px;
    bottom: 10px;
    border: 2px solid rgba(255,215,0,0.1);
    border-radius: 20px;
    pointer-events: none;
    z-index: 0;
}

/* 当前玩家指示器 */
.current-turn {
    animation: pulse 1s ease-in-out infinite;
}

