    .middle-area {
        padding: 0 20px;
    }

    .btn {
        padding: 10px 20px;
        font-size: 14px;
    }

    .player-info {
        padding: 8px 15px;
        font-size: 12px;
    }
}

/* 动画效果 */
@keyframes dealCard {
    0% {
        opacity: 0;
        transform: scale(0.3) rotate(180deg) translateY(-100px);
    }
    50% {
        opacity: 0.8;
    }
    100% {
        opacity: 1;
        transform: scale(1) rotate(0deg) translateY(0);
    }
}

.card.dealing {
    animation: dealCard 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes playCard {
    0% {
        transform: scale(1.3);
        opacity: 0.5;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

.played-cards .card {
    animation: playCard 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes pulse {
    0%, 100% {
        box-shadow: 0 0 20px rgba(255,215,0,0.4);
    }
    50% {
        box-shadow: 0 0 40px rgba(255,215,0,0.6);
    }
}

.card.selected {
    animation: pulse 1.5s ease-in-out infinite;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.player-info {
    animation: fadeIn 0.5s ease-out;
}

/* 添加装饰性边框 */
.game-container::before {
    content: '';
    position: absolute;
    top: 10px;
    left: 10px;
    right: 10px;
    bottom: 10px;
    border: 2px solid rgba(255,215,0,0.1);
    border-radius: 20px;
    pointer-events: none;
    z-index: 0;
}

/* 当前玩家指示器 */
.current-turn {
    animation: pulse 1s ease-in-out infinite;
}
