    font-size: 14px;
    border: 1px solid rgba(255,215,0,0.2);
    box-shadow: 0 8px 30px rgba(0,0,0,0.5);
}

.player-name {
    font-weight: 600;
    font-size: 15px;
    color: #ffffff;
}

.card-count {
    color: #ffd700;
    font-weight: 600;
    text-shadow: 0 0 10px rgba(255,215,0,0.4);
}

.role {
    color: #ff8585;
    font-weight: bold;
    padding: 3px 12px;
    border-radius: 12px;
    background: rgba(255,85,85,0.25);
    border: 1px solid rgba(255,85,85,0.3);
}

.role.landlord {
    color: #ffd700;
    background: rgba(255,215,0,0.15);
    border-color: rgba(255,215,0,0.4);
    text-shadow: 0 0 12px rgba(255,215,0,0.6);
}

.role.farmer {
    color: #4ecdc4;
    background: rgba(78,205,196,0.15);
    border-color: rgba(78,205,196,0.3);
}

/* 中间区域布局 */
.middle-area {
    flex: 1;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 80px;
}

.table-area {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    max-width: 600px;
}

.table-cards {
    background: linear-gradient(135deg, rgba(15,50,30,0.9), rgba(25,70,40,0.7));
