    text-align: center;
    font-weight: 600;
    text-shadow: 0 2px 15px rgba(0,0,0,0.8);
}

/* 扑克牌样式 */
.card {
    width: 70px;
    height: 98px;
    background: linear-gradient(145deg, #e8e8e8, #d8d8d8);
    border-radius: 10px;
    display: inline-flex;
    flex-direction: column;
    align-items: flex-start;
    justify-content: flex-start;
    padding: 6px 8px;
    margin: 0 -8px;
    box-shadow: 0 5px 20px rgba(0,0,0,0.4), inset 0 1px 0 rgba(255,255,255,0.8);
    cursor: pointer;
    transition: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
    position: relative;
    border: 1px solid #bbb;
}

.card:hover {
    transform: translateY(-15px) scale(1.05);
    box-shadow: 0 20px 45px rgba(0,0,0,0.5), inset 0 1px 0 rgba(255,255,255,0.9);
