    display: flex;
    gap: 20px;
    margin-top: 20px;
}

.btn {
    padding: 14px 36px;
    font-size: 16px;
    font-weight: 700;
    border: none;
    border-radius: 28px;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 8px 25px rgba(0,0,0,0.5);
    position: relative;
    overflow: hidden;
}

.btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s;
}

.btn:hover::before {
    left: 100%;
}

.btn:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 35px rgba(0,0,0,0.6);
}

.btn:active {
    transform: translateY(-2px);
}

.btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
}

.btn-start {
    background: linear-gradient(135deg, #2a8a6e, #1d5f4a);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-call {
    background: linear-gradient(135deg, #c99100, #8b5a00);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-pass {
    background: linear-gradient(135deg, #5a6a6e, #3d4d50);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-play {
    background: linear-gradient(135deg, #a83226, #7a2218);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-hint {
    background: linear-gradient(135deg, #6b3fa0, #4a2b70);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

