    font-size: 14px;
    font-weight: bold;
    writing-mode: vertical-rl;
}

/* 小王大王特殊样式 */
.card.joker-small {
    background: linear-gradient(145deg, #ffd5d5, #ffaaaa);
    color: #2c2c2c;
}

.card.joker-big {
    background: linear-gradient(145deg, #ffe566, #ffcc00);
    color: #6b0000;
}

/* 玩家手牌区域 */
.bottom-player .player-cards {
    display: flex;
    justify-content: center;
    align-items: flex-end;
    min-height: 110px;
    margin-bottom: 15px;
    perspective: 1000px;
}

/* 电脑玩家手牌（背面） */
.computer-card {
    width: 45px;
    height: 63px;
    background: linear-gradient(145deg, #2d5a7a, #1e3d5a);
    border-radius: 6px;
    margin: 0 -18px;
    border: 2px solid rgba(255,255,255,0.2);
    box-shadow: 0 4px 15px rgba(0,0,0,0.5);
}

/* 出过的牌 */
.played-cards {
    min-height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 15px 0;
}

.played-cards .card {
    margin: 0 -20px;
    cursor: default;
}

.played-cards .card:hover {
    transform: none;
}

.pass-text {
    color: rgba(200,200,200,0.8);
    font-size: 18px;
    font-weight: 700;
    letter-spacing: 4px;
    text-shadow: 0 2px 10px rgba(0,0,0,0.8);
}

/* 控制按钮区域 */
.control-area {
    display: flex;
    gap: 20px;
    margin-top: 20px;
}

.btn {
    padding: 14px 36px;
    font-size: 16px;
    font-weight: 700;
    border: none;
    border-radius: 28px;
    cursor: pointer;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 0 8px 25px rgba(0,0,0,0.5);
    position: relative;
    overflow: hidden;
}

.btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s;
}

.btn:hover::before {
    left: 100%;
}

.btn:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 35px rgba(0,0,0,0.6);
}

.btn:active {
    transform: translateY(-2px);
}

.btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
}

.btn-start {
    background: linear-gradient(135deg, #2a8a6e, #1d5f4a);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-call {
    background: linear-gradient(135deg, #c99100, #8b5a00);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-pass {
    background: linear-gradient(135deg, #5a6a6e, #3d4d50);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-play {
    background: linear-gradient(135deg, #a83226, #7a2218);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

.btn-hint {
    background: linear-gradient(135deg, #6b3fa0, #4a2b70);
    color: #ffffff;
    text-shadow: 0 1px 3px rgba(0,0,0,0.5);
}

/* 底牌区域 */
.bottom-cards-area {
    position: absolute;
    top: 15px;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    align-items: center;
    gap: 12px;
    background: linear-gradient(135deg, rgba(20,20,20,0.95), rgba(35,35,35,0.9));
    padding: 12px 25px;
    border-radius: 25px;
    border: 1px solid rgba(255,215,0,0.2);
    box-shadow: 0 8px 30px rgba(0,0,0,0.5);
}

.bottom-cards-label {
    color: #e0e0e0;
    font-size: 15px;
    font-weight: 600;
}

.bottom-cards {
    display: flex;
    gap: 8px;
}

.bottom-cards .card {
    width: 50px;
    height: 70px;
    margin: 0;
    font-size: 12px;
}

/* 左右玩家特殊布局 */
.left-player, .right-player {
    flex-direction: row;
}

.left-player .player-cards,
.right-player .player-cards {
    flex-direction: column;
    min-width: 50px;
}

.left-player .computer-card,
.right-player .computer-card {
    margin: -12px 0;
}

/* 响应式设计 */
@media (max-width: 768px) {
    .card {
        width: 50px;
        height: 70px;
        margin: 0 -15px;
    }

    .card-value {
        font-size: 14px;
    }

    .card-suit {
        font-size: 12px;
    }

    .middle-area {
        padding: 0 20px;
    }

    .btn {
        padding: 10px 20px;
        font-size: 14px;
    }

    .player-info {
        padding: 8px 15px;
        font-size: 12px;
    }
}

/* 动画效果 */
@keyframes dealCard {
    0% {
        opacity: 0;
        transform: scale(0.3) rotate(180deg) translateY(-100px);
    }
    50% {
        opacity: 0.8;
    }
    100% {
        opacity: 1;
        transform: scale(1) rotate(0deg) translateY(0);
    }
}

.card.dealing {
    animation: dealCard 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes playCard {
    0% {
        transform: scale(1.3);
        opacity: 0.5;
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

.played-cards .card {
    animation: playCard 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

@keyframes pulse {
    0%, 100% {
        box-shadow: 0 0 25px rgba(255,215,0,0.5), 0 0 50px rgba(255,215,0,0.2);
    }
    50% {
        box-shadow: 0 0 40px rgba(255,215,0,0.7), 0 0 80px rgba(255,215,0,0.3);
    }
}

.card.selected {
    animation: pulse 1.5s ease-in-out infinite;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.player-info {
    animation: fadeIn 0.5s ease-out;
}

/* 装饰性边框 */
.game-container::before {
    content: '';
    position: absolute;
    top: 10px;
    left: 10px;
    right: 10px;
    bottom: 10px;
    border: 2px solid rgba(255,215,0,0.15);
    border-radius: 20px;
    pointer-events: none;
    z-index: 0;
}

/* 当前玩家指示器 */
.current-turn {
    animation: pulse 1s ease-in-out infinite;
}
