        .arch-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            height: 3px;
            background: linear-gradient(90deg, var(--accent-cyan), var(--accent-green));
            transform: scaleX(0);
            transition: transform 0.3s ease;
        }

        .arch-card:hover {
            transform: translateY(-5px);
            box-shadow: var(--glow-cyan);
            border-color: var(--accent-cyan);
        }

        .arch-card:hover::before {
            transform: scaleX(1);
        }

        .arch-card h3 {
            color: var(--accent-green);
            margin-bottom: 0.5rem;
            font-size: 1.2rem;
        }

        .arch-card p {
            color: var(--text-secondary);
            font-size: 0.95rem;
        }

        .arch-card .path {
            display: block;
            color: var(--accent-purple);
            font-size: 0.85rem;
            margin-top: 1rem;
            font-family: 'Fira Code', monospace;
        }

        /* ReAct Flow */
        .react-flow {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 2rem;
            margin: 3rem 0;
            flex-wrap: wrap;
        }

        .flow-step {
            background: var(--bg-tertiary);
            border: 2px solid var(--border-color);
            border-radius: 12px;
            padding: 1.5rem 2rem;
            text-align: center;
            min-width: 150px;
            transition: all 0.3s ease;
        }

        .flow-step:hover {
            border-color: var(--accent-cyan);
            box-shadow: var(--glow-cyan);
            transform: scale(1.05);
        }

        .flow-step.thought { border-color: var(--accent-purple); }
        .flow-step.action { border-color: var(--accent-cyan); }
        .flow-step.observation { border-color: var(--accent-green); }

        .flow-step span {
            display: block;
            font-size: 1.5rem;
            margin-bottom: 0.5rem;
        }

        .flow-arrow {
            font-size: 2rem;
