        .tool-badge:hover {
            background: var(--bg-secondary);
            border-color: var(--accent-cyan);
            box-shadow: var(--glow-cyan);
        }

        .tool-badge .name {
            color: var(--accent-cyan);
            font-weight: bold;
            font-size: 1.1rem;
        }

        .tool-badge .desc {
            color: var(--text-secondary);
            font-size: 0.85rem;
            margin-top: 0.3rem;
        }

        /* Code Block */
        .code-block {
            background: var(--bg-secondary);
            border: 1px solid var(--border-color);
            border-radius: 8px;
            overflow: hidden;
            margin: 2rem 0;
        }

        .code-header {
            background: var(--bg-tertiary);
