            font-weight: bold;
            font-size: 1.2rem;
        }

        /* Footer */
        .footer {
            background: var(--bg-secondary);
            border-top: 1px solid var(--border-color);
            padding: 2rem 0;
            text-align: center;
            margin-top: 4rem;
        }

        .footer p {
            color: var(--text-secondary);
            font-size: 0.9rem;
        }

        .footer .terminal-prompt {
            color: var(--accent-green);
            font-weight: bold;
        }

        /* Responsive */
        @media (max-width: 768px) {
            .hero h1 {
                font-size: 2rem;
            }

            .logo {
                font-size: 1.8rem;
            }

            .react-flow {
                flex-direction: column;
                gap: 1rem;
            }

