            -webkit-text-fill-color: transparent;
            background-clip: text;
        }

        /* 输入框样式 */
        .apple-input {
            background: #ffffff;
            border: 1px solid #d2d2d7;
            transition: all 0.3s ease;
        }

        .apple-input:focus {
            outline: none;
            border-color: #0071e3;
            box-shadow: 0 0 0 4px rgba(0, 113, 227, 0.2);
        }

        /* 技能条动画 */
        @keyframes fillBar {
